SourceCode.Declaration:
/// <summary>
/// Tracks the active form context (data source, record, navigation fields) and raises
/// change events consumed by the Copilot host control to pass ERP context to the agent.
/// </summary>
public final class COTXCopilotHostFormContext
{
    FormRun         formRun;
    FormDataSource  rootFormDataSource;
    Common          rootFormRecord;

    public FormName         formCaption;
    public DataAreaId       dataAreaId;
    public FormName         curFormName;
    public MenuItemName     curMenuItemName;
    public str              formMode;
    public TableName        tableName;
    public FieldId          naturalKeyId;
    public FieldLabel       naturalKey;
    public FieldValue       naturalValue;

}

Method.Source:
protected void new(FormRun _formRun)
{
    //TODO: Initialize this from cache

    formRun     = _formRun;
    curFormName = _formRun.name();
    formCaption = formRun.design().caption();
    dataAreaId  = curExt();

    if (formRun.args()
        && formRun.args().menuItemName())
    {
        MenuFunction callerMenuFunction = new MenuFunction(formRun.args().menuItemName(), formRun.args().menuItemType());

        if (callerMenuFunction
            && callerMenuFunction.label())
        {
            curMenuItemName = SysLabel::labelId2String(callerMenuFunction.label());
        }
    }


    this.subscribeToRootDataSource(_formRun);

    if (rootFormDataSource)
    {
        SysDictTable sysDictTable = SysDictTable::newTableId(rootFormDataSource.table());

        tableName = sysDictTable.labelDefined() ? sysDictTable.label() : sysDictTable.name();

        if (SysDictTable.titleField1())
        {
            naturalKeyId = sysDictTable.titleField1();
            SysDictField sysDictField = new SysDictField(sysDictTable.id(), naturalKeyId);

            naturalKey = sysDictField.labelDefined() ? sysDictField.label() : sysDictField.name();
            this.setKeyValue();
        }
    }
}

public static COTXCopilotHostFormContext construct(FormRun _formRun)
{
    COTXCopilotHostFormContext formContext = new COTXCopilotHostFormContext(_formRun);

    return formContext;
}

private void subscribeToRootDataSource(FormRun _formRun)
{
    for (int i = 1; i <= _formRun.dataSourceCount(); i++)
    {
        rootFormDataSource  = _formRun.dataSource(i);
        rootFormRecord      = rootFormDataSource.cursor();

        if ((_formRun.design().titleDatasource() && rootFormDataSource.id() == _formRun.design().titleDatasource()) ||
            (!_formRun.design().titleDatasource() && rootFormDataSource.isRoot()))
        {
            rootFormDataSource.OnActivated += eventhandler(this.rootDatasourceActive);

            break;
        }
    }
}

private void rootDatasourceActive(FormDataSource _ds, FormDataSourceEventArgs e)
{
    rootFormRecord = _ds.cursor();
    this.setKeyValue();
    this.onFormContextChange(this);
}

/// <summary>
/// Delegate raised when the form context changes, e.g. when the active record changes.
/// </summary>
/// <param name="_sender">The form context instance that changed.</param>
delegate void onFormContextChange(COTXCopilotHostFormContext _sender)
{
}
